mapper.insert:

insert into ecs_consumer (cId, name, sex, 
  userId, pwd, phone, 
  birthday, address, note
  )
values (#{cid,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, #{sex,jdbcType=INTEGER}, 
  #{userid,jdbcType=VARCHAR}, #{pwd,jdbcType=VARCHAR}, #{phone,jdbcType=VARCHAR}, 
  #{birthday,jdbcType=TIMESTAMP}, #{address,jdbcType=VARCHAR}, #{note,jdbcType=VARCHAR}
  )

